




#include <iostream>
#include <ctime>
#include <bits/stdc++.h>
using namespace std ;


int lcs(string str1, string str2, int str1Idx, int str2Idx, vector<vector<int>> &dp) {
  if(str1.length() == 0 || str2.length() == 0 || str1Idx < 0|| str2Idx < 0) return 0 ;

  
  // check before computing
  if(dp[str1Idx][str2Idx] != -1) return dp[str1Idx][str2Idx] ;

  if(str1[str1Idx] == str2[str2Idx]) {
    return dp[str1Idx][str2Idx] = 1 + lcs(str1, str2, str1Idx -1, str2Idx - 1, dp) ;
  } else {
    return max(lcs(str1, str2, str1Idx - 1, str2Idx, dp), lcs(str1, str2, str1Idx, str2Idx - 1, dp)) ;
  }

}


int main() {
  /*string str1 = "" ;
  string str2 = "" ;
  */

  string str1 = "abcde";
  string str2 = "ace" ;
  time_t start, end;

  time(&start) ;

  vector<vector<int>> dp(str1.length() +1, vector<int>(str2.length() +1, -1));
  cout << lcs(str1, str2, str1.length() -1, str2.length() -1, dp) ;
  
  time(&end) ;

  cout << "time taken: " << setprecision(5) <<  double(end - start) << endl;

  return 0;
}
